DROP TABLE IF EXISTS `customer`;
CREATE TABLE `customer` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` int(10) unsigned NOT NULL,
  `name` varchar(200) NOT NULL,
  `pic` varchar(200) NOT NULL,
  `email` varchar(50) NOT NULL,
  `address` text NOT NULL,
  `customercategory_id` int(10) unsigned NOT NULL,
  `file_status` tinyint(2) DEFAULT NULL,
  `created_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `nama_sales` varchar(200) DEFAULT NULL,
  `area` varchar(250) DEFAULT NULL,
  `segmen` varchar(100) DEFAULT NULL,
  `id_product` int(10) DEFAULT NULL,
  `type_service` varchar(200) DEFAULT NULL,
  `koordinat_lat` varchar(100) DEFAULT NULL,
  `koordinat_long` varchar(100) DEFAULT NULL,
  `terminasi` varchar(200) DEFAULT NULL,
  `kapasitas` int(11) DEFAULT NULL,
  `mix` varchar(100) DEFAULT NULL,
  `satuan` varchar(100) DEFAULT NULL,
  `kapasitas1` int(10) DEFAULT NULL,
  `mix1` varchar(100) DEFAULT NULL,
  `satuan1` varchar(100) DEFAULT NULL,
  `interface` varchar(200) DEFAULT NULL,
  `media` varchar(200) DEFAULT NULL,
  `exisiting` varchar(255) DEFAULT NULL,
  `budget` varchar(250) DEFAULT NULL,
  `progress` int(11) DEFAULT NULL,
  `id_customer_presales` int(11) DEFAULT NULL,
  `list_data_filename` varchar(250) DEFAULT NULL,
  `list_data_path` varchar(200) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `fk_customer_user1_idx` (`user_id`),
  KEY `fk_customer_customercategory1_idx` (`customercategory_id`),
  CONSTRAINT `fk_customer_customercategory1` FOREIGN KEY (`customercategory_id`) REFERENCES `customercategory` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `fk_customer_user1` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=latin1;

